        history = model.fit(
            X_train,
            to_categorical(y_train),
            validation_data=(X_val, to_categorical(y_val)),
            epochs=15,
            batch_size=32,
            # verbose=0
        )

        # Evaluate the model.
        loss, accuracy = model.evaluate(
         testin,
         to_categorical(testout)
        )
        
